# Collection of all curated examples
CURATED_EXAMPLES = {
    'HUMAN_HBB': HUMAN_HBB,
    'HUMAN_TP53': HUMAN_TP53,
    'ECOLI_K12_FRAGMENT': ECOLI_K12_FRAGMENT,
    'MYCOPLASMA_GENITALIUM': MYCOPLASMA_GENITALIUM,
    'HPV16_COMPLETE': HPV16_COMPLETE,
    'AAV2_COMPLETE': AAV2_COMPLETE
}

def get_curated_example(name: str) -> dict:
    """
    Get a specific curated example by name.
    
    Args:
        name: Name of the example (e.g., 'HUMAN_HBB', 'HUMAN_TP53', etc.)
        
    Returns:
        Dictionary with sequence information
    """
    return CURATED_EXAMPLES.get(name.upper(), {})

def list_curated_examples() -> list:
    """
    List all available curated examples.
    
    Returns:
        List of example names
    """
    return list(CURATED_EXAMPLES.keys())

def get_all_curated_sequences() -> dict:
    """
    Get all curated examples.
    
    Returns:
        Dictionary of all curated examples
    """
    return CURATED_EXAMPLES.copy()

